    # Funcion setCustom, encargada de establecer parametros personalizados para la ejecucion.
    def setCustom(self, name, value):
        if name in ('topos', 'switches', 'hosts', 'controllers'):
            param = name.upper()
            globals()[param].update(value)
        elif name == 'validate':
            self.validate = value
        else:
            globals()[name] = value

    # Funcion parseCustomFile, para analizar el archivo personalizado y a√±ade los parametros antes de analizar las
    # opciones de la linea de comandos.
    def parseCustomFile(self, fileName):
        customs = {}
        if os.path.isfile(fileName):
            with open(fileName, 'r') as f:
                exec(f.read())
            for name, val in customs.items():
                self.setCustom(name, val)
        else:
            raise Exception('could not find custom file: %s' % fileName)


